# 分数数据
# scores = [0, 529, 1225, 64,1521, 1444, 81]
scores = [44,	44,	12,	12,	]

# 将数据转换为DataFrame
df = pd.DataFrame(user_behavior_data)
scores_series = pd.Series(scores)

# 归一化特征
scaler = StandardScaler()
df_scaled = pd.DataFrame(scaler.fit_transform(df), columns=df.columns)

# 计算相关性和误差指标
results = {
    'Pearson Coeff': [],
    'MSE': [],
    'MAE': []
}

for col in df.columns:
    pearson_coeff, _ = pearsonr(df[col], scores_series)
    mse = mean_squared_error(scores_series, df[col])
    mae = mean_absolute_error(scores_series, df[col])

    results['Pearson Coeff'].append(pearson_coeff)
    results['MSE'].append(mse)
    results['MAE'].append(mae)

results_df = pd.DataFrame(results, index=df.columns)

# 线性回归分析
X = sm.add_constant(df)  # 添加常数项
model = sm.OLS(scores_series, X).fit()

# 输出结果
print("Pearson 相关系数、MSE 和 MAE：")
print(results_df)

print("\n线性回归分析结果：")
print(model.summary())